                    <div class=\"container\">
                        <div class=\"carousel-caption text-left\">
                            <h1>{{ header.title }}</h1>
                            <p>{{ header.content }}</p>
                            <p><a class=\"btn btn-lg btn-primary\" href=\"{{ header.btnUrl }}\" role=\"button\">{{ header.btnTitle }}</a></p>
                        </div>
                    </div>
                </div>
